mapper.insert:

	SELECT #{buyerLgu} ||
	LPAD(NVL(TO_NUMBER(SUBSTR(MAX(BUYER_ID),5)),0)
	+1,2,'0')
	FROM BUYER
	WHERE BUYER_LGU = #{buyerLgu}

