mapper.select:
SELECT PROPERTY_NAME, PROPERTY_VALUE, DESCRIPTION
FROM CUSTOM_PROPS
WHERE PROPERTY_NAME = #{propertyName}

mapper.insert:
INSERT INTO CUSTOM_PROPS
(
	PROPERTY_NAME
	, PROPERTY_VALUE
	, DESCRIPTION
)VALUES(
	#{propertyName,jdbcType=VARCHAR}
	, #{propertyValue,jdbcType=VARCHAR}
	, #{description,jdbcType=VARCHAR} 
)

mapper.update:
	INSERT INTO custom_props (
    			property_name
   				 , property_value
  				 , description
				) VALUES (
     			 #{propertyName,jdbcType=VARCHAR}
				, #{propertyValue,jdbcType=VARCHAR}
				, #{description,jdbcType=VARCHAR}
				)
